function getMixin(vm){
    const {title} = vm.$options;
    if(title){
        return typeof title === 'function' ? title.call(vm) : title;
    }
}

const serverMixin = {
    created(){
        const title = getMixin(this);
        if(title){
            this.$ssrContext.title = title;
        }
    }
}

const clientMixin = {
    mounted() {
        const title = getMixin(this);
        if(title) {
            document.title = title;
        }
    }
}

export default process.env.VUE_ENV === 'server' ? serverMixin : clientMixin

import Vue from 'vue'

import App from './App.vue'
import { createRouter } from './router'
import { createStore } from './store'
import { sync } from "vuex-router-sync";
import vuetify from './plugins/vuetify'
import '@babel/polyfill'
import 'roboto-fontface/css/roboto/roboto-fontface.css'
import '@mdi/font/css/materialdesignicons.css'

// import './plugins/axios'
// import './plugins/moment'
import './plugins'
import titleMixin from './mixins/title-mixin';

Vue.config.productionTip = false

export function createApp(ctx) {
	const router = createRouter();
	const store = createStore();
	sync(store, router);

	Vue.mixin(titleMixin);

	const app = new Vue({
		data: { url: ctx ? ctx.url : '' },
		router,
		store,
		vuetify,
		render: h => h(App)
	});

	return { app, router, store };
}

import { createApp } from './main';

export default (ctx) => {
	return new Promise(async (resolve, reject) => {
		const { app, router, store } = createApp(ctx);
		await store.dispatch('appInit', ctx);
		router.push(ctx.url); //router/index.js
		console.log('ctx : ', ctx);
		router.onReady(() => {
			
			ctx.rendered = () => ctx.state = store.state;
			const matchedComponents = router.getMatchedComponents();
			if (matchedComponents.length === 0) {
				return reject({ code: 404 });
			}
			return resolve(app);
		}, reject);
	});
}

const level = {
    LV : {
        BLOCK : 0,
        AWAIT : 1,
        MEMBER : 2,
        VIP : 4,
        ADMIN : 6,
        SUPER : 10,
    },
    LV_LABEL : (lv) =>{
        if(lv >= level.LV.SUPER) {
            return '최고관리자'
        } else if(level.LV.ADMIN <= lv && lv < lebel.LV.SUPER) {
            return '관리자'
        } else if(level.LV.VIP <= lv && lv < lebel.LV.ADMIN) {
            return '우수회원'
        } else if(level.LV.MEMBER <= lv && lv < lebel.LV.VIP) {
            return '일반회원'
        } else if(level.LV.AWAIT == lv) {
            return '대기회원'
        } else {
            return '차단 / 비 회원'
        }
    },
    LV_COLOR : (lv) =>{
        if(lv >= level.LV.SUPER) {
            return 'light-blue accent-4'
        } else if(level.LV.ADMIN <= lv && lv < lebel.LV.SUPER) {
            return 'green lighten-1'
        } else if(level.LV.VIP <= lv && lv < lebel.LV.ADMIN) {
            return 'green lighten-2'
        } else if(level.LV.MEMBER <= lv && lv < lebel.LV.VIP) {
            return 'lime lighten-3'
        } else if(level.LV.AWAIT == lv) {
            return 'red accent-2'
        } else {
            return 'red accent-4'
        }
    },
    isGrant(req, lv) {
		return req.user && req.user.mb_level >= lv ? true : false;
	}
}

module.exports = level;

